import { nanoid } from "nanoid";
import deleteTripRequest from "../api/deleteTripRequest";
import {useQueryClient, useMutation} from "react-query";
import { useState } from "react";
import style from "./style.css"



export default function DestinationList({ trips, isLoading, user}){
    const queryClient = useQueryClient();

    
    const {mutate: deleteTrip} = useMutation(
        (tripToDelete)=>deleteTripRequest(tripToDelete),{
            onSettled: ()=>{
                queryClient.invalidateQueries("trips")
            }
        }
    )
    const [listOpen, setListOpen] = useState(true)
    const setList = () =>{
        setListOpen(!listOpen)
    }

    const handleCountryDelete = (location) => {
        trips.map((trip)=>{
            if(trip.tripLocation == location && trip.username == user){
                deleteTrip(trip)
                return;
            }
        })
    }

    const generateTripDash = (trip) => {
        const t = trip
    }
            
    return(
        <div>
            <button onClick={setList}>Look at Trips</button>
            {listOpen && 
            <div className="countriesPanel">
            {!isLoading && trips.map((trip)=>(
                <div key={nanoid()} className="text-3xl font-bold underline">{trip.tripLocation}
                    <button onClick={()=>{handleCountryDelete(trip.tripLocation)}}>Delete</button>
                    <button onClick={()=>{generateTripDash(trip)}}>View Trip</button>
                </div>
                
                
            ))}
            </div>
            }
        </div>
    )
}
