import ImageCollector from "./ImageCollector.js"

export default function Info({country, start, end, caption}){

    const handleImageError = (e) =>{
        e.target.onerror = null;
        e.target.style.display = "none"
    }

    try{
        const image = require(`./UserImages/${country}/${country}.jpg`)

    } catch{
        const image = require("./UserImages/download.jpg")
    }

    return(
        <div>
            {country}
            <br>
            </br>
            {start} - {end}
            <br/>
            {caption}
            <img src={image} width={"200px"} 
            onError = {handleImageError}
            />

        </div>
    )

}
