import {useLoadScript, GoogleMap, MarkerF} from "@react-google-maps/api";
import { useCallback, useMemo, useState, useRef, useEffect } from "react";
import {useQuery} from "react-query";
import "./styles/map.css"
import Places from "./Places"
import readMarkerRequest from "../api/readMarkerRequest";
import { library } from "../config";
import addMarkerRequest from "../api/addMarkerRequest";
import {useMutation, useQueryClient} from "react-query";
import PlaceList from "./PlaceList";
import deleteMarkerRequest from "../api/deleteMarkerRequest";
import PlaceItem from "./PlaceItem";
import {nanoid} from "nanoid";



export default function Map() {
  const mapRef = useRef(3);
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
    libraries: library,
  });

  const {isLoading, data: markers} = useQuery(
    'markers', 
    readMarkerRequest
    )

  const center = useMemo(() => ({ lat: 24.52043, lng: 15.856743 }), []);
  const options = useMemo(()=> ({
    disableDefaultUI: true,
    clickableIcons: false,
    mapId: "5c60bef575d03ff",})
    ,[])


  const queryClient = useQueryClient();

  const {mutate: addMarker} = useMutation(
      (newMarker)=>addMarkerRequest(newMarker),
      {
        onSettled: ()=>{
          queryClient.invalidateQueries("markers")
        }
      }
  )

  const {mutate: deleteMarker} = useMutation(
    (markerToDelete)=>deleteMarkerRequest(markerToDelete),
    {
      onSettled: ()=>{
        queryClient.invalidateQueries("markers")
      }
    }
)
  const onLoad = useCallback((map)=>(mapRef.current = map), []);

  const [isHovering, setIsHovering] = useState(false)
  const handleMouseOver = () => {
    setIsHovering(true)
  }
  const handleMouseOut = () => {
    setIsHovering(false)
  }

  const [coords, setCoords] = useState({x:0,y:0})


    const handleWindowMouseMove = (event) =>{
      setTimeout(5000)
      setCoords({
        x: event.clientX,
        y: event.clientY,
      })
    }
  
  window.addEventListener("mousemove", handleWindowMouseMove)

  return (
    <div style={{"position":"relative"}}>
      {!isLoaded && isLoading ? (
        <h1>Loading...</h1>
      ) : (
        
        <div className="container">
          <div className="control">
            <div className="addingPanel">
            <h1>Add A Destination</h1>
            <Places setLocation={(position, country)=>{
              mapRef.current?.panTo(position);
              mapRef.current?.setZoom(4);
              let newMarker = {latLngLit: position, markerCountry:country}
              addMarker(newMarker)
            }}/>
          </div>
          <div className="countriesPanel">
            {!isLoading && markers.map((marker)=>(
              <PlaceItem name={marker.country} 
              handleCountryDelete={(country) => {
                markers.map((marker)=>{
                  if(marker.country == country){
                    deleteMarker(marker)
                    return;
                  }
                })
              }} 
              key={nanoid()}/>
            ))}
          </div>


        </div>
        <GoogleMap
          mapContainerClassName="map-container"
          center={center}
          zoom={3}
          options={options}
          onLoad={onLoad}
          > 
            {!isLoading && markers.map((marker)=>(
              <MarkerF position={{lat: marker.latitude,lng: marker.longitude}} key={marker._id} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}/>
              
            ))}
        </GoogleMap>
        {isHovering && (
          <div style={{"position":"absolute","top":(coords.y -25),"left":coords.y-100}}>{coords.x},{coords.y}</div>
        )}
        

        </div>
        
      )}
    </div>
  );
};
