import {useLoadScript, GoogleMap, MarkerF} from "@react-google-maps/api";
import { useCallback, useMemo, useState, useRef } from "react";
import {useQuery} from "react-query";
import "./styles/map.css"
import Places from "./Places"
import readMarkerRequest from "../api/readMarkerRequest";
import { library } from "../config";
import addMarkerRequest from "../api/addMarkerRequest";
import {useMutation, useQueryClient} from "react-query";
import PlaceList from "./PlaceList";


export default function Map() {
  const [location, setLocation] = useState([]);
  const mapRef = useRef(3);
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
    libraries: library,
  });

  const list = []
  const {isLoading, data: markers} = useQuery(
    'markers', 
    readMarkerRequest, {
      onSuccess: (markers)=> markers.map((marker)=>(
        updateCountryList(marker.country)
      ))
    }
    )

  const updateCountryList = (newCountry) => {
    let tempList = countryList
    tempList.push(newCountry)
    setCountryList(tempList)
    console.log(countryList)
  }

  const center = useMemo(() => ({ lat: 24.52043, lng: 15.856743 }), []);
  const options = useMemo(()=> ({
    disableDefaultUI: true,
    clickableIcons: false,
    mapId: "5c60bef575d03ff",})
    ,[])


  const queryClient = useQueryClient();

  const {mutate: addMarker} = useMutation(
      (newMarker)=>addMarkerRequest(newMarker),
      {
        onSettled: ()=>{
          queryClient.invalidateQueries("markers")
        }
      }
  )
  const onLoad = useCallback((map)=>(mapRef.current = map), []);
  let [countryList, setCountryList] = useState([])

  
  
  return (
    <div>
      {!isLoaded && isLoading ? (
        <h1>Loading...</h1>
      ) : (
        <div className="container">
          <div className="control">
            <div className="addingPanel">
            <h1>Add A Destination</h1>
            <Places setLocation={(position, country)=>{
              setLocation(position);
              mapRef.current?.panTo(position);
              mapRef.current?.setZoom(4);
              let newMarker = {latLngLit: position, markerCountry:country}
              addMarker(newMarker)
              setCountryList([])
            }}/>
          </div>
          <div className="countriesPanel">
            <PlaceList countryList = {countryList}/>
          </div>


        </div>
        <GoogleMap
          mapContainerClassName="map-container"
          center={center}
          zoom={3}
          options={options}
          onLoad={onLoad}
          > 
            {!isLoading && markers.map((marker)=>(
              <MarkerF position={{lat: marker.latitude,lng: marker.longitude}} key={marker._id}/>
              
            ))}
        </GoogleMap>

        </div>
        
      )}
    </div>
  );
};
