import { useQueryClient, useMutation } from "react-query";
import addMarkerRequest from "../api/addMarkerRequest";
import Places from "./Places";
import { useState } from "react";

export default function DestinationAdder({mapRef, user}){

    const queryClient = useQueryClient();

    const {mutate: addMarker} = useMutation(
        (newMarker)=>addMarkerRequest(newMarker),{
            onSettled: ()=>{
                queryClient.invalidateQueries("markers")
            }
        }
    )
    const [formOpen, setFormOpen] = useState(true)
    return(
        
        <div className="addingPanel">
            <Places 
            user={user}
            setLocation={(position, country, start, end, caption, user)=>{
                mapRef.current?.panTo(position);
                mapRef.current?.setZoom(4);
                let newMarker = {latLngLit: position, markerCountry:country, startDate: start, endDate: end, caption: caption, username: user}
                addMarker(newMarker)
            }}/>
        </div>
    )
}
