import { useQuery } from "react-query"
import readMarkerRequest from "../api/readMarkerRequest";



const {isLoading, data: markers} = useQuery(
    'markers', 
    readMarkerRequest
)



var countryList = [];
markers.map((marker)=>(
    countryList.push(marker.country)
))




CollectedImages = {
    "image1": require(`./UserImages/${countryList[0]}`)
}
